      "fields": {
        "jobPurpose": "",
        "location": "<p>\n    Charlotte, NC\n</p>",
        "travelExpectation": "<p>\n    15%\n</p>",
        "jobResponsibilities": "<ul>\n    <li>Manage the technical implementation of the PeraHealth solution with multiple customers, including:\n        <ul>\n            <li>Facilitate new customer kick off meetings for the purposes of gathering requirements, timeline planning and discovery\n            </li>\n            <li>Create and maintain detailed project plans\n            </li>\n            <li>Continuously monitor progress in terms of the status of the project and report back to internal management and subject matter team members and the customer using weekly status reports\n            </li>\n            <li>Assist with escalated customer issues and project issues as identified or requested by the customer through direct support or through the support of the PeraHealth subject matter experts assigned to the project\n            </li>\n            <li>Facilitate Post Implementation Review meetings with the Client\n            </li>\n        </ul>\n    </li>\n    <li>Drive technical projects and provide leadership in an innovative and fast paced environment.\n    </li>\n    <li>Take responsibility for overall planning, execution and success of complex technical projects.\n    </li>\n    <li>Facilitate ongoing technical support of the PeraHealth solution along with any upgrades\n    </li>\n    <li>Travel to customer sites within the US is required up to 15%\n    </li>\n    <li>Support other customer related projects that require project management services\n    </li>\n    <li>Support internal department projects\n    </li>\n    <li>Provide mentoring and support to other team members\n    </li>\n    <li>Manage team of implementation systems engineers\n    </li>\n</ul>",
        "qualifications": "<ul>\n    <li>5+ years experience in project management of software implementations\n    </li>\n    <li>Experience with needs assessment, software design, implementation, testing and validation, support, and reporting\n    </li>\n    <li>Proven ability to effectively communicate with Executive Sponsors and IT\n    </li>\n    <li>Leadership, facilitation, team building, and change management skills a must\n    </li>\n    <li>Proven ability to manage a team of technical employees\n    </li>\n    <li>Ability to listen to clients in order to deliver high quality customer service\n    </li>\n    <li>EHR/EMR and/or clinical experience a plus\n    </li>\n    <li>SQL knowledge and/or Tableau experience preferred\n    </li>\n    <li>Minimum of bachelorâs degree\n    </li>\n    <li>Salesforce.com and Smartsheet.com experience a plus\n    </li>\n</ul>",
        "additionalSkills": "<p>\n    <br>\n</p>\n<ul>\n    <li>Experience working and managing projects with remote technical teams\n    </li>\n    <li>Strong interpersonal skills including the ability to work collaboratively and manage across team boundaries and drive organizational improvements\n    </li>\n    <li>Excellent verbal and written communication skills\n    </li>\n</ul>",
        "applicationDetails": "<p>\n    To apply for this position, please <a href=\"mailto:recruiting@perahealth.com\">click here</a> to email your cover letter and resume to <a href=\"mailto:recruiting@perahealth.com\">recruiting@perahealth.com</a>.\n</p>",
        "footnote": "<p>\n    <strong>PeraHealth is an Equal Opportunity Employer</strong>\n</p>",
        "title": "Project Manager - Implementations",
        "slug": "project-manager-implementations"
      }
