      "fields": {
        "jobPurpose": "",
        "location": "<p>\n    Charlotte, NC\n</p>",
        "travelExpectation": "<p>\n    10%\n</p>",
        "jobResponsibilities": "<ul>\n    <li>Lead successful technical implementations of the PeraHealth solutions including: provide technical leadership and recommendations, provide guidance on infrastructure requirements, install and configure PeraHealth software, implement data interfaces, validate and QA the solution for delivery to the customer\n    </li>\n    <li>Align closely with the Implementation Project Managers\n    </li>\n    <li>Handle multiple installations in parallel and in different stages of implementation\n    </li>\n    <li>Provide technical guidance to the clinical deployment team during the solution roll-out to the clinical teams\n    </li>\n    <li>Lead upgrades and deployment of PeraHealth updates and testing upgrades to the EHR systems\n    </li>\n    <li>Provide ongoing support and troubleshooting of production issues of the PeraHealth solution\n    </li>\n    <li>Travel to customer site as needed (~ 10%)\n    </li>\n</ul>",
        "qualifications": "<ul>\n    <li>1-3+ years of experience in as a systems engineer and/or implementing software\n    </li>\n    <li>Experience with EHR implementations such as Epic, Cerner or Allscripts\n    </li>\n    <li>Experience with HL7 or web services data interfaces\n    </li>\n    <li>Experience with databases and SQL\n    </li>\n    <li>Experience with installing database backed web applications with interactive front ends (Java, Spring, CSS, Javascript, Apache, Tomcat)\n    </li>\n    <li>Love of troubleshooting and puzzle solving\n    </li>\n    <li>Experience with LDAP\n    </li>\n    <li>Minimum of bachelorâs degree in Computer Science or Engineering\n    </li>\n</ul>",
        "additionalSkills": "<ul>\n    <li>Experience working with remote teams\n    </li>\n    <li>Outstanding interpersonal skills including the ability to effectively communicate with persons throughout the organization\n    </li>\n    <li>Ability to work collaboratively\n    </li>\n    <li>Ability to analyze and resolve complex issues\n    </li>\n    <li>Excellent verbal and written communication skills\n    </li>\n</ul>",
        "applicationDetails": "<p>\n    To apply for this position, please <a href=\"mailto:recruiting@perahealth.com\">click here</a> to email your cover letter and resume to <a href=\"mailto:recruiting@perahealth.com\">recruiting@perahealth.com</a>.\n</p>",
        "footnote": "<p>\n    <strong>PeraHealth is an Equal Opportunity Employer</strong>\n</p>",
        "title": "Implementation Systems Engineer",
        "slug": "implementation-systems-engineer"
      }
